with q_docLen as (select f.id_document, sum(f.count) docLen from document_facts f inner join author_dimension ad on ad.id_author = f.id_author where gender = 'male' group by f.id_document ), q_noDocWords as (select f.id_word, count(distinct f.id_document) noDocWords from document_facts f inner join author_dimension ad on ad.id_author = f.id_author where gender = 'male' group by f.id_word) select wd.word, sum(f.tf * (1+ln(dl.docLen/ndw.noDocWords))) TFIDF from document_facts f inner join author_dimension ad on ad.id_author = f.id_author inner join word_dimension wd on wd.id_word = f.id_word inner join q_noDocWords ndw on ndw.id_word = f.id_word cross join (select count(id_document) docLen from q_docLen) dl where ad.gender = 'male' group by wd.word order by TFIDF desc limit 10;
